import React from "react";
import tableroImg from "../assets/tablero.png";

// Posiciones aproximadas para 6 fichas en 3 anillos.
// Ajustá estos valores después según tu imagen real.
const TOKEN_POSITIONS = {
  0: [ // Expectativas (anillo exterior)
    { top: "12%", left: "50%" },
    { top: "22%", left: "78%" },
    { top: "50%", left: "88%" },
    { top: "78%", left: "78%" },
    { top: "88%", left: "50%" },
    { top: "78%", left: "22%" }
  ],
  1: [ // Contribución (anillo medio)
    { top: "24%", left: "50%" },
    { top: "34%", left: "70%" },
    { top: "50%", left: "76%" },
    { top: "66%", left: "70%" },
    { top: "76%", left: "50%" },
    { top: "66%", left: "30%" }
  ],
  2: [ // Desafíos (anillo interior / centro)
    { top: "38%", left: "50%" },
    { top: "44%", left: "62%" },
    { top: "50%", left: "68%" },
    { top: "56%", left: "62%" },
    { top: "62%", left: "50%" },
    { top: "50%", left: "38%" }
  ]
};

function Board({ players, currentRound }) {
  return (
    <div className="board-wrapper">
      <div className="board">
        <img src={tableroImg} alt="Tablero Tándem" className="board-bg" />

        {players.map((p, index) => {
          const roundToShow =
            p.maxRoundReached >= 0 ? p.maxRoundReached : null;

          if (roundToShow === null) return null;

          const pos =
            TOKEN_POSITIONS[roundToShow]?.[index] ||
            TOKEN_POSITIONS[0][index];

          return (
            <div
              key={p.id}
              className="token"
              style={{
                top: pos.top,
                left: pos.left,
                backgroundColor: p.color,
                opacity: roundToShow === currentRound ? 1 : 0.6
              }}
              title={p.name}
            />
          );
        })}
      </div>
    </div>
  );
}

export default Board;
