// src/components/BikeIcon.jsx
import React from "react";

function BikeIcon({ color, active }) {
  return (
    <svg
      viewBox="0 0 64 40"
      className={`bike-icon ${active ? "bike-icon--active" : ""}`}
    >
      {/* Ruedas */}
      <circle className="bike-wheel" cx="14" cy="26" r="9" />
      <circle className="bike-wheel" cx="46" cy="26" r="9" />

      {/* Cuadro */}
      <path
        className="bike-frame"
        d="M14 26 L22 12 L32 12 L40 26 L30 26 L22 12"
        stroke={color}
        strokeWidth="2.5"
        fill="none"
        strokeLinejoin="round"
      />

      {/* Manubrio */}
      <path
        d="M32 12 L38 8"
        stroke={color}
        strokeWidth="2"
        strokeLinecap="round"
      />

      {/* Asiento */}
      <path
        d="M22 12 L18 8"
        stroke={color}
        strokeWidth="2"
        strokeLinecap="round"
      />

      {/* Centro de las ruedas */}
      <circle cx="14" cy="26" r="2" fill={color} />
      <circle cx="46" cy="26" r="2" fill={color} />
    </svg>
  );
}

export default BikeIcon;
