import React, { useState, useMemo } from "react";
import "./styles.css";

import PlayerSetup from "./components/PlayerSetup";
import Board from "./components/Board";
import TurnPanel from "./components/TurnPanel";
import Summary from "./components/Summary";

const ROUNDS = ["EXPECTATIVAS", "CONTRIBUCIÓN", "DESAFÍOS"];

const BASE_PLAYERS = [
  { id: "rojo", label: "Rojo", color: "#E53935" },
  { id: "naranja", label: "Naranja", color: "#FB8C00" },
  { id: "amarillo", label: "Amarillo", color: "#FDD835" },
  { id: "verde", label: "Verde", color: "#43A047" },
  { id: "azul", label: "Azul", color: "#1E88E5" },
  { id: "violeta", label: "Violeta", color: "#8E24AA" }
];

function App() {
  const [phase, setPhase] = useState("setup"); // "setup" | "playing" | "summary"
  const [players, setPlayers] = useState(
    BASE_PLAYERS.map((p) => ({
      ...p,
      name: p.label,
      active: false,
      expectativas: "",
      contribucion: "",
      desafios: "",
      maxRoundReached: -1
    }))
  );

  const [currentRound, setCurrentRound] = useState(0); // 0,1,2
  const [currentPlayerIndex, setCurrentPlayerIndex] = useState(0);

  const activePlayers = useMemo(
    () => players.filter((p) => p.active),
    [players]
  );

  const startGame = () => {
    if (activePlayers.length === 0) {
      alert("Activá al menos un jugador para comenzar.");
      return;
    }
    setCurrentRound(0);
    setCurrentPlayerIndex(0);
    setPhase("playing");
  };

  const updatePlayerField = (playerId, field, value) => {
    setPlayers((prev) =>
      prev.map((p) =>
        p.id === playerId
          ? {
              ...p,
              [field]: value,
              maxRoundReached:
                field === "expectativas"
                  ? Math.max(p.maxRoundReached, 0)
                  : field === "contribucion"
                  ? Math.max(p.maxRoundReached, 1)
                  : field === "desafios"
                  ? Math.max(p.maxRoundReached, 2)
                  : p.maxRoundReached
            }
          : p
      )
    );
  };

  const goToNextTurn = () => {
    if (activePlayers.length === 0) return;

    const lastPlayerIndex = activePlayers.length - 1;
    const lastRoundIndex = ROUNDS.length - 1;

    // siguiente jugador
    if (currentPlayerIndex < lastPlayerIndex) {
      setCurrentPlayerIndex((prev) => prev + 1);
      return;
    }

    // pasamos de ronda
    if (currentRound < lastRoundIndex) {
      setCurrentRound((prev) => prev + 1);
      setCurrentPlayerIndex(0);
      return;
    }

    // última ronda + último jugador → fin
    setPhase("summary");
  };

  const resetGame = () => {
    setPlayers(
      BASE_PLAYERS.map((p) => ({
        ...p,
        name: p.label,
        active: false,
        expectativas: "",
        contribucion: "",
        desafios: "",
        maxRoundReached: -1
      }))
    );
    setCurrentRound(0);
    setCurrentPlayerIndex(0);
    setPhase("setup");
  };

  const roundLabel = ROUNDS[currentRound] ?? "";

  // seguridad extra: si por algún motivo no hay jugadores activos en medio del juego
  if (phase === "playing" && activePlayers.length === 0) {
    return (
      <div className="app">
        <h1 className="title">Tándem – Versión Digital</h1>
        <div className="panel">
          <p>Se quedaron sin jugadores activos. Volvé a configurar para jugar.</p>
          <button className="primary-btn" onClick={resetGame}>
            Volver al inicio
          </button>
        </div>
      </div>
    );
  }

  const currentPlayer =
    phase === "playing" ? activePlayers[currentPlayerIndex] : null;

  return (
    <div className="app">
      <h1 className="title">Tándem – Versión Digital</h1>

      {phase === "setup" && (
        <>
          <PlayerSetup players={players} setPlayers={setPlayers} />
          <button className="primary-btn" onClick={startGame}>
            Comenzar Juego
          </button>
        </>
      )}

      {phase === "playing" && currentPlayer && (
        <div className="game-layout">
          <Board
            players={activePlayers}
            currentRound={currentRound}
            currentPlayerId={currentPlayer.id}
          />
          <TurnPanel
            currentRound={currentRound}
            roundLabel={roundLabel}
            players={activePlayers}
            currentPlayerIndex={currentPlayerIndex}
            onSaveAnswer={updatePlayerField}
            onNextTurn={goToNextTurn}
          />
        </div>
      )}

      {phase === "summary" && (
        <Summary players={activePlayers} onRestart={resetGame} rounds={ROUNDS} />
      )}
    </div>
  );
}

export default App;
