import React, { useState, useEffect } from "react";

const FIELD_BY_ROUND = ["expectativas", "contribucion", "desafios"];
const LABEL_BY_ROUND = [
  "¿Cuáles son tus EXPECTATIVAS para este proyecto?",
  "¿Qué CONTRIBUCIONES podés aportar?",
  "¿Qué DESAFÍOS u obstáculos ves?"
];

function TurnPanel({
  roundLabel,
  players,
  currentPlayerIndex,
  onSaveAnswer,
  onNextTurn
}) {
  const player = players[currentPlayerIndex];
  const roundIndex = FIELD_BY_ROUND.indexOf(
    FIELD_BY_ROUND.find((_, i) => i === FIELD_BY_ROUND.indexOf(FIELD_BY_ROUND[0]) + 0) // truco solo para forzar el uso del array
  );
  const field = FIELD_BY_ROUND[roundLabel === "EXPECTATIVAS" ? 0 : roundLabel === "CONTRIBUCIÓN" ? 1 : 2];

  const label =
    roundLabel === "EXPECTATIVAS"
      ? LABEL_BY_ROUND[0]
      : roundLabel === "CONTRIBUCIÓN"
      ? LABEL_BY_ROUND[1]
      : LABEL_BY_ROUND[2];

  const [text, setText] = useState(player[field] || "");

  useEffect(() => {
    // Cuando cambia de jugador o ronda, reseteamos el texto al valor guardado
    setText(player[field] || "");
  }, [player, field]);

  const handleSave = () => {
    if (!text.trim()) return;
    onSaveAnswer(player.id, field, text.trim());
    onNextTurn();
  };

  return (
    <div className="panel turn-panel">
      <h2>Ronda: {roundLabel}</h2>
      <h3 style={{ color: player.color }}>Turno de: {player.name}</h3>

      <p className="hint">{label}</p>

      <textarea
        value={text}
        onChange={(e) => setText(e.target.value)}
        rows={5}
        placeholder="Escribí tu respuesta acá..."
      />

      <button className="primary-btn" onClick={handleSave}>
        Guardar y pasar al siguiente
      </button>
    </div>
  );
}

export default TurnPanel;
