// src/components/Board.jsx
import React, { useMemo, useState, useEffect } from "react";
import boardImg from "../assets/board-magic.png";

// Bicicletas (ajustá paths/nombres según tu estructura real)
import bikeYellow from "../assets/bikes/bike-yellow.png";
import bikeRed from "../assets/bikes/bike-red.png";
import bikeBlue from "../assets/bikes/bike-blue.png";
import bikeGreen from "../assets/bikes/bike-green.png";
import bikeOrange from "../assets/bikes/bike-orange.png";
import bikePurple from "../assets/bikes/bike-purple.png";

// IMPORTANTE: ajustá estos hex a los colores reales que uses en los jugadores
const COLOR_TO_BIKE = {
  "#fbbc04": bikeYellow, // amarillo
  "#ea4335": bikeRed, // rojo
  "#4285f4": bikeBlue, // azul
  "#34a853": bikeGreen, // verde
  "#ff6d00": bikeOrange, // naranja
  "#aa00ff": bikePurple // violeta
};

// Distancia al centro por ronda (en % del tamaño del tablero)
const RADII = [40, 28, 16]; // expectativas, contribución, desafíos

function Board({ players, currentRound, currentPlayerId }) {
  const [clicked, setClicked] = useState(false);

  // efecto "cámara" al hacer click en el tablero
  useEffect(() => {
    if (!clicked) return;
    const t = setTimeout(() => setClicked(false), 250);
    return () => clearTimeout(t);
  }, [clicked]);

  // calcula posiciones de las bicis en el tablero
  const positions = useMemo(() => {
    const n = players.length || 1;

    return players.map((player, index) => {
      const angle = (2 * Math.PI * index) / n - Math.PI / 2;

      const stage = Math.max(
        0,
        Math.min(2, player.maxRoundReached ?? 0)
      );

      const radius = RADII[stage];

      // centro del tablero (un poco más abajo por la composición de la imagen)
      const cx = 50;
      const cy = 57;

      const x = cx + radius * Math.cos(angle);
      const y = cy + radius * Math.sin(angle);

      return {
        left: `${x}%`,
        top: `${y}%`
      };
    });
  }, [players]);

  return (
    <div className="board-wrapper">
      <div
        className={
          "board board-image" + (clicked ? " board--pulse" : "")
        }
        onClick={() => setClicked(true)}
      >
        {/* Fondo del tablero con pequeño movimiento de “cámara” */}
        <div className="board-bg-wrapper">
          <img
            src={boardImg}
            alt="Tablero Tándem"
            className="board-bg"
          />
        </div>

        {/* Capa de efectos: glow por ronda + partículas mágicas */}
        <div className="board-effects">
          <div
            className={`board-glow board-glow--round-${currentRound}`}
          />
          {/* Partículas mágicas */}
          <div className="board-particles board-particles--a" />
          <div className="board-particles board-particles--b" />
        </div>

        {/* Bicicletas de los jugadores */}
        {players.map((player, index) => {
          const bikeSrc =
            COLOR_TO_BIKE[player.color] || bikeYellow;
          const isActive = player.id === currentPlayerId;

          return (
            <img
              key={player.id}
              src={bikeSrc}
              alt={`Bicicleta de ${player.name || "Jugador"}`}
              className={
                "bike-token" +
                (isActive ? " bike-token--active" : "")
              }
              style={positions[index]}
            />
          );
        })}
      </div>
    </div>
  );
}

export default Board;
