// src/components/Bike.jsx
import React, { useRef } from "react";
import { useFrame } from "@react-three/fiber";
import * as THREE from "three";

export default function Bike({ color, target, isActive }) {
  const ref = useRef();

  useFrame((state, delta) => {
    if (!ref.current) return;

    // Interpolación suave hacia el target
    ref.current.position.lerp(target, 3 * delta);

    // Rebote estilo cartoon si está activo
    const t = state.clock.getElapsedTime();
    const bounce = isActive ? Math.sin(t * 4) * 0.15 : 0;
    ref.current.position.y = target.y + bounce;

    // Apunta al centro del tablero
    ref.current.lookAt(0, target.y, 0);
  });

  return (
    <group ref={ref} position={target}>
      
      {/* Sombra corregida — usando planeGeometry */}
      <mesh
        receiveShadow
        rotation-x={-Math.PI / 2}
        position={[0, -0.35, 0]}
        scale={[0.9, 0.35, 1]}
      >
        <planeGeometry args={[1, 1]} />
        <meshBasicMaterial color="black" transparent opacity={0.35} />
      </mesh>

      {/* ======== RUEDAS ======== */}

      {/* Rueda izquierda */}
      <mesh castShadow position={[-0.55, 0, 0]}>
        <cylinderGeometry args={[0.22, 0.22, 0.15, 24]} />
        <meshStandardMaterial color="black" />
      </mesh>

      {/* Rueda derecha */}
      <mesh castShadow position={[0.55, 0, 0]}>
        <cylinderGeometry args={[0.22, 0.22, 0.15, 24]} />
        <meshStandardMaterial color="black" />
      </mesh>

      {/* ======== CUADRO PRINCIPAL ======== */}

      <mesh castShadow position={[0, 0.12, 0]}>
        <boxGeometry args={[1.2, 0.18, 0.15]} />
        <meshStandardMaterial
          color={color}
          emissive={isActive ? color : "#000000"}
          emissiveIntensity={isActive ? 0.6 : 0}
        />
      </mesh>

      {/* Poste del asiento */}
      <mesh castShadow position={[-0.35, 0.25, 0]}>
        <boxGeometry args={[0.08, 0.3, 0.08]} />
        <meshStandardMaterial color={color} />
      </mesh>

      {/* Asiento */}
      <mesh castShadow position={[-0.35, 0.43, 0]}>
        <boxGeometry args={[0.4, 0.15, 0.25]} />
        <meshStandardMaterial color={color} />
      </mesh>

      {/* Poste del manubrio */}
      <mesh castShadow position={[0.5, 0.25, 0]}>
        <boxGeometry args={[0.08, 0.3, 0.08]} />
        <meshStandardMaterial color={color} />
      </mesh>

      {/* Manubrio */}
      <mesh castShadow position={[0.5, 0.43, 0]}>
        <boxGeometry args={[0.45, 0.15, 0.25]} />
        <meshStandardMaterial color={color} />
      </mesh>

    </group>
  );
}
