import React from "react";
import BikeIcon from "./BikeIcon";

// Colores inspirados en el tablero físico
const RING_COLORS = {
  expectativas: "#FCD135", // amarillo
  contribucion: "#3E73C9", // azul
  desafios: "#E84137" // rojo
};

// Posiciones aproximadas para 6 jugadores en cada anillo
const TOKEN_POSITIONS = {
  0: [ // Expectativas – exterior
    { top: "8%", left: "50%" },
    { top: "22%", left: "86%" },
    { top: "50%", left: "94%" },
    { top: "78%", left: "86%" },
    { top: "92%", left: "50%" },
    { top: "78%", left: "14%" }
  ],
  1: [ // Contribución – medio
    { top: "22%", left: "50%" },
    { top: "32%", left: "76%" },
    { top: "50%", left: "82%" },
    { top: "68%", left: "76%" },
    { top: "78%", left: "50%" },
    { top: "68%", left: "24%" }
  ],
  2: [ // Desafíos – interior
    { top: "34%", left: "50%" },
    { top: "42%", left: "62%" },
    { top: "50%", left: "68%" },
    { top: "58%", left: "62%" },
    { top: "66%", left: "50%" },
    { top: "50%", left: "38%" }
  ]
};

function Board({ players, currentRound, currentPlayerId }) {
  return (
    <div className="board-wrapper">
      <div className="board">
        {/* Tablero SVG más “físico” */}
        <svg viewBox="0 0 100 100" className="board-svg">
          {/* Base cuadrada */}
          <rect
            x="2"
            y="2"
            width="96"
            height="96"
            rx="6"
            fill="#3f3f48"
          />

          {/* Marco exterior */}
          <rect
            x="4"
            y="4"
            width="92"
            height="92"
            rx="6"
            fill="#2f2f36"
            stroke="#111"
            strokeWidth="0.8"
          />

          {/* Anillo EXPECTATIVAS – exterior */}
          <circle
            cx="50"
            cy="50"
            r="38"
            fill="none"
            stroke={RING_COLORS.expectativas}
            strokeWidth={currentRound === 0 ? 7 : 5}
            className={`ring ${currentRound === 0 ? "ring--active" : ""}`}
          />

          {/* Anillo CONTRIBUCIÓN – medio */}
          <circle
            cx="50"
            cy="50"
            r="27"
            fill="none"
            stroke={RING_COLORS.contribucion}
            strokeWidth={currentRound === 1 ? 7 : 5}
            className={`ring ${currentRound === 1 ? "ring--active" : ""}`}
          />

          {/* Anillo DESAFÍOS – interior */}
          <circle
            cx="50"
            cy="50"
            r="16"
            fill="none"
            stroke={RING_COLORS.desafios}
            strokeWidth={currentRound === 2 ? 7 : 5}
            className={`ring ${currentRound === 2 ? "ring--active" : ""}`}
          />

          {/* Centro blanco como en el tablero */}
          <circle cx="50" cy="50" r="7" fill="#FFFFFF" />

          {/* Textos similares al PDF */}
          {/* EXPECTATIVAS arriba y abajo */}
          <text
            x="50"
            y="10"
            textAnchor="middle"
            fontSize="4"
            fill="#FFFFFF"
            letterSpacing="1"
          >
            EXPECTATIVAS
          </text>
          <text
            x="50"
            y="96"
            textAnchor="middle"
            fontSize="4"
            fill="#FFFFFF"
            letterSpacing="1"
            transform="rotate(180 50 96)"
          >
            EXPECTATIVAS
          </text>

          {/* CONTRIBUCIÓN izquierda y derecha */}
          <text
            x="6"
            y="52"
            textAnchor="middle"
            fontSize="3.6"
            fill="#FFFFFF"
            transform="rotate(-90 6 52)"
          >
            CONTRIBUCIÓN
          </text>
          <text
            x="94"
            y="52"
            textAnchor="middle"
            fontSize="3.6"
            fill="#FFFFFF"
            transform="rotate(90 94 52)"
          >
            CONTRIBUCIÓN
          </text>

          {/* DESAFÍOS en diagonal aprox */}
          <text
            x="24"
            y="20"
            textAnchor="middle"
            fontSize="3.4"
            fill="#FFFFFF"
            transform="rotate(-30 24 20)"
          >
            DESAFÍOS
          </text>
          <text
            x="76"
            y="80"
            textAnchor="middle"
            fontSize="3.4"
            fill="#FFFFFF"
            transform="rotate(150 76 80)"
          >
            DESAFÍOS
          </text>
        </svg>

        {/* Bicicletas */}
        {players.map((p, index) => {
          const roundToShow =
            p.maxRoundReached >= 0 ? p.maxRoundReached : null;
          if (roundToShow === null) return null;

          const pos =
            TOKEN_POSITIONS[roundToShow]?.[index] ||
            TOKEN_POSITIONS[0][index];

          const isActive =
            p.id === currentPlayerId && roundToShow === currentRound;

          return (
            <div
              key={p.id}
              className={`token ${isActive ? "token--active" : ""}`}
              style={{
                top: pos.top,
                left: pos.left
              }}
              title={p.name}
            >
              <BikeIcon color={p.color} active={isActive} />
            </div>
          );
        })}
      </div>
    </div>
  );
}

export default Board;
