// src/components/Board.jsx
import React from "react";
import BikeIcon from "./BikeIcon";

// Colores inspirados en el tablero físico
const RING_COLORS = {
  expectativas: "#FCD135", // amarillo
  contribucion: "#3E73C9", // azul
  desafios: "#E84137" // rojo
};

// Posiciones aproximadas para 6 jugadores en cada anillo
const TOKEN_POSITIONS = {
  0: [
    { top: "10%", left: "50%" },
    { top: "24%", left: "83%" },
    { top: "50%", left: "90%" },
    { top: "76%", left: "83%" },
    { top: "90%", left: "50%" },
    { top: "76%", left: "17%" }
  ],
  1: [
    { top: "20%", left: "50%" },
    { top: "32%", left: "72%" },
    { top: "50%", left: "80%" },
    { top: "68%", left: "72%" },
    { top: "80%", left: "50%" },
    { top: "68%", left: "28%" }
  ],
  2: [
    { top: "32%", left: "50%" },
    { top: "40%", left: "62%" },
    { top: "50%", left: "68%" },
    { top: "60%", left: "62%" },
    { top: "68%", left: "50%" },
    { top: "50%", left: "38%" }
  ]
};

function Board({ players, currentRound, currentPlayerId }) {
  return (
    <div className="board-wrapper">
      <div className="board">
        {/* Tablero SVG */}
        <svg viewBox="0 0 100 100" className="board-svg">
          {/* Fondo */}
          <rect
            x="2"
            y="2"
            width="96"
            height="96"
            rx="8"
            fill="#4B4B55"
          />

          {/* Anillo EXPECTATIVAS */}
          <circle
            cx="50"
            cy="50"
            r="38"
            fill="none"
            stroke={RING_COLORS.expectativas}
            strokeWidth={currentRound === 0 ? 8 : 6}
            className={`ring ${currentRound === 0 ? "ring--active" : ""}`}
          />

          {/* Anillo CONTRIBUCIÓN */}
          <circle
            cx="50"
            cy="50"
            r="26"
            fill="none"
            stroke={RING_COLORS.contribucion}
            strokeWidth={currentRound === 1 ? 8 : 6}
            className={`ring ${currentRound === 1 ? "ring--active" : ""}`}
          />

          {/* Anillo DESAFÍOS */}
          <circle
            cx="50"
            cy="50"
            r="14"
            fill="none"
            stroke={RING_COLORS.desafios}
            strokeWidth={currentRound === 2 ? 8 : 6}
            className={`ring ${currentRound === 2 ? "ring--active" : ""}`}
          />

          {/* Centro blanco */}
          <circle cx="50" cy="50" r="6" fill="#FFFFFF" />

          {/* Textos aproximados (NO hace falta que sean exactos al tablero físico) */}
          <text
            x="50"
            y="8"
            textAnchor="middle"
            fontSize="5"
            fill="#FFFFFF"
          >
            EXPECTATIVAS
          </text>
          <text
            x="50"
            y="18"
            textAnchor="middle"
            fontSize="4"
            fill="#FFFFFF"
          >
            CONTRIBUCIÓN
          </text>
          <text
            x="50"
            y="28"
            textAnchor="middle"
            fontSize="4"
            fill="#FFFFFF"
          >
            DESAFÍOS
          </text>
        </svg>

        {/* Bicicletas */}
        {players.map((p, index) => {
          const roundToShow =
            p.maxRoundReached >= 0 ? p.maxRoundReached : null;
          if (roundToShow === null) return null;

          const pos =
            TOKEN_POSITIONS[roundToShow]?.[index] ||
            TOKEN_POSITIONS[0][index];

          const isActive =
            p.id === currentPlayerId && roundToShow === currentRound;

          return (
            <div
              key={p.id}
              className={`token ${isActive ? "token--active" : ""}`}
              style={{
                top: pos.top,
                left: pos.left
              }}
              title={p.name}
            >
              <BikeIcon color={p.color} active={isActive} />
            </div>
          );
        })}
      </div>
    </div>
  );
}

export default Board;
