// src/components/Bike.jsx
import React, { useRef } from "react";
import { useFrame } from "@react-three/fiber";
import * as THREE from "three";

export default function Bike({ color, target, isActive }) {
  const ref = useRef();

  useFrame((state, delta) => {
    if (!ref.current) return;

    // Mover hacia la posición objetivo (Interpolación suave)
    ref.current.position.lerp(target, 3 * delta);

    // Rebote si está activo
    const t = state.clock.getElapsedTime();
    const bounce = isActive ? Math.sin(t * 4) * 0.15 : 0;
    ref.current.position.y = target.y + bounce;

    // Mirar hacia el centro
    ref.current.lookAt(0, target.y, 0);
  });

  return (
    <group ref={ref} position={target}>
      {/* Sombra */}
      <mesh receiveShadow rotation-x={-Math.PI / 2} position={[0, -0.35, 0]}>
        <ellipseGeometry args={[0.55, 0.25, 20]} />
        <meshBasicMaterial color="black" transparent opacity={0.35} />
      </mesh>

      {/* Rueda Izquierda */}
      <mesh castShadow position={[-0.55, 0, 0]}>
        <cylinderGeometry args={[0.22, 0.22, 0.15, 24]} />
        <meshStandardMaterial color="black" />
      </mesh>

      {/* Rueda Derecha */}
      <mesh castShadow position={[0.55, 0, 0]}>
        <cylinderGeometry args={[0.22, 0.22, 0.15, 24]} />
        <meshStandardMaterial color="black" />
      </mesh>

      {/* Cuadro */}
      <mesh castShadow position={[0, 0.12, 0]}>
        <boxGeometry args={[1.2, 0.18, 0.15]} />
        <meshStandardMaterial
          color={color}
          emissive={isActive ? color : "#000"}
          emissiveIntensity={isActive ? 0.6 : 0}
        />
      </mesh>

      {/* Asiento */}
      <mesh castShadow position={[-0.35, 0.4, 0]}>
        <boxGeometry args={[0.4, 0.12, 0.25]} />
        <meshStandardMaterial color={color} />
      </mesh>

      {/* Manubrio */}
      <mesh castShadow position={[0.35, 0.4, 0]}>
        <boxGeometry args={[0.4, 0.12, 0.25]} />
        <meshStandardMaterial color={color} />
      </mesh>
    </group>
  );
}
