import React, { useState, useEffect } from 'react';
import { Trophy, Users, Clock, Play, LogIn, Zap, Crown, Target, Sparkles } from 'lucide-react';
import './App.css';

const CATEGORIES = {
  historia: { name: 'Historia', color: 'from-yellow-400 to-orange-500', emoji: 'ðŸ“š', icon: 'ðŸ›ï¸' },
  ciencia: { name: 'Ciencia', color: 'from-green-400 to-emerald-600', emoji: 'ðŸ”¬', icon: 'âš—ï¸' },
  deportes: { name: 'Deportes', color: 'from-orange-400 to-red-500', emoji: 'âš½', icon: 'ðŸ†' },
  geografia: { name: 'GeografÃ­a', color: 'from-blue-400 to-cyan-500', emoji: 'ðŸŒ', icon: 'ðŸ—ºï¸' },
  entretenimiento: { name: 'Entretenimiento', color: 'from-pink-400 to-purple-500', emoji: 'ðŸŽ¬', icon: 'ðŸŽ­' },
  arte: { name: 'Arte', color: 'from-purple-400 to-indigo-600', emoji: 'ðŸŽ¨', icon: 'ðŸ–¼ï¸' }
};

const QUESTIONS = {
  historia: [
    { q: 'Â¿En quÃ© aÃ±o comenzÃ³ la Primera Guerra Mundial?', a: ['1914', '1918', '1920', '1939'], c: 0 },
    { q: 'Â¿QuiÃ©n fue el primer presidente de Estados Unidos?', a: ['Thomas Jefferson', 'George Washington', 'Abraham Lincoln', 'John Adams'], c: 1 },
    { q: 'Â¿En quÃ© aÃ±o cayÃ³ el Muro de BerlÃ­n?', a: ['1987', '1988', '1989', '1990'], c: 2 },
    { q: 'Â¿QuÃ© imperio construyÃ³ Machu Picchu?', a: ['Azteca', 'Maya', 'Inca', 'Olmeca'], c: 2 },
  ],
  ciencia: [
    { q: 'Â¿CuÃ¡l es el planeta mÃ¡s grande del sistema solar?', a: ['Saturno', 'Neptuno', 'JÃºpiter', 'Urano'], c: 2 },
    { q: 'Â¿QuÃ© elemento quÃ­mico tiene el sÃ­mbolo "Au"?', a: ['Plata', 'Oro', 'Hierro', 'Cobre'], c: 1 },
    { q: 'Â¿CuÃ¡ntos huesos tiene el cuerpo humano adulto?', a: ['186', '206', '226', '246'], c: 1 },
    { q: 'Â¿CuÃ¡l es la velocidad de la luz?', a: ['300,000 km/s', '150,000 km/s', '450,000 km/s', '600,000 km/s'], c: 0 },
  ],
  deportes: [
    { q: 'Â¿CuÃ¡ntos jugadores tiene un equipo de fÃºtbol en el campo?', a: ['9', '10', '11', '12'], c: 2 },
    { q: 'Â¿En quÃ© paÃ­s se originÃ³ el tenis?', a: ['Inglaterra', 'Francia', 'Estados Unidos', 'EspaÃ±a'], c: 1 },
    { q: 'Â¿CuÃ¡ntos anillos olÃ­mpicos hay?', a: ['4', '5', '6', '7'], c: 1 },
    { q: 'Â¿CuÃ¡ntos Grand Slams hay en el tenis?', a: ['3', '4', '5', '6'], c: 1 },
  ],
  geografia: [
    { q: 'Â¿CuÃ¡l es la capital de Australia?', a: ['SÃ­dney', 'Melbourne', 'Canberra', 'Brisbane'], c: 2 },
    { q: 'Â¿CuÃ¡l es el rÃ­o mÃ¡s largo del mundo?', a: ['Nilo', 'Amazonas', 'YangtsÃ©', 'Misisipi'], c: 1 },
    { q: 'Â¿CuÃ¡ntos continentes hay?', a: ['5', '6', '7', '8'], c: 2 },
    { q: 'Â¿CuÃ¡l es el paÃ­s mÃ¡s grande del mundo?', a: ['CanadÃ¡', 'China', 'Estados Unidos', 'Rusia'], c: 3 },
  ],
  entretenimiento: [
    { q: 'Â¿QuiÃ©n dirigiÃ³ la pelÃ­cula "Titanic"?', a: ['Steven Spielberg', 'James Cameron', 'Martin Scorsese', 'Christopher Nolan'], c: 1 },
    { q: 'Â¿CuÃ¡ntos Oscar ganÃ³ "El SeÃ±or de los Anillos: El Retorno del Rey"?', a: ['9', '10', '11', '12'], c: 2 },
    { q: 'Â¿En quÃ© aÃ±o se estrenÃ³ la primera pelÃ­cula de Harry Potter?', a: ['1999', '2000', '2001', '2002'], c: 2 },
    { q: 'Â¿QuiÃ©n interpretÃ³ a Iron Man en el MCU?', a: ['Chris Evans', 'Robert Downey Jr.', 'Chris Hemsworth', 'Mark Ruffalo'], c: 1 },
  ],
  arte: [
    { q: 'Â¿QuiÃ©n pintÃ³ "La Mona Lisa"?', a: ['Miguel Ãngel', 'Leonardo da Vinci', 'Rafael', 'Donatello'], c: 1 },
    { q: 'Â¿En quÃ© museo se encuentra "La noche estrellada" de Van Gogh?', a: ['Louvre', 'MoMA', 'Prado', 'British Museum'], c: 1 },
    { q: 'Â¿CuÃ¡ntas sinfonÃ­as compuso Beethoven?', a: ['7', '8', '9', '10'], c: 2 },
    { q: 'Â¿QuiÃ©n esculpiÃ³ "El David"?', a: ['Donatello', 'Miguel Ãngel', 'Bernini', 'Rodin'], c: 1 },
  ]
};

// SimulaciÃ³n de almacenamiento (en producciÃ³n usarÃ­as una base de datos real)
const storage = {
  data: {},
  get: function(key) {
    return this.data[key] ? { value: this.data[key] } : null;
  },
  set: function(key, value) {
    this.data[key] = value;
    return { value };
  },
  delete: function(key) {
    delete this.data[key];
    return true;
  }
};

function App() {
  const [screen, setScreen] = useState('login');
  const [playerName, setPlayerName] = useState('');
  const [roomCode, setRoomCode] = useState('');
  const [currentRoom, setCurrentRoom] = useState(null);
  const [players, setPlayers] = useState([]);
  const [gameStarted, setGameStarted] = useState(false);
  const [currentQuestion, setCurrentQuestion] = useState(null);
  const [selectedAnswer, setSelectedAnswer] = useState(null);
  const [timeLeft, setTimeLeft] = useState(15);
  const [scores, setScores] = useState({});
  const [showResult, setShowResult] = useState(false);
  const [questionNumber, setQuestionNumber] = useState(0);
  const [gameFinished, setGameFinished] = useState(false);
  const [animate, setAnimate] = useState(false);
  const [streakBonus, setStreakBonus] = useState(0);

  useEffect(() => {
    if (gameStarted && currentQuestion && timeLeft > 0 && !showResult) {
      const timer = setTimeout(() => setTimeLeft(timeLeft - 1), 1000);
      return () => clearTimeout(timer);
    } else if (timeLeft === 0 && !showResult) {
      handleAnswerSubmit();
    }
  }, [timeLeft, gameStarted, currentQuestion, showResult]);

  const createRoom = () => {
    if (!playerName.trim()) return;
    const code = Math.random().toString(36).substr(2, 6).toUpperCase();
    const roomData = {
      code,
      host: playerName,
      players: [playerName],
      started: false
    };
    storage.set(`room_${code}`, JSON.stringify(roomData));
    setCurrentRoom(code);
    setPlayers([playerName]);
    setScores({ [playerName]: 0 });
    setScreen('lobby');
  };

  const joinRoom = () => {
    if (!playerName.trim() || !roomCode.trim()) return;
    const roomResult = storage.get(`room_${roomCode.toUpperCase()}`);
    if (!roomResult) {
      alert('Sala no encontrada');
      return;
    }
    const roomData = JSON.parse(roomResult.value);
    if (roomData.started) {
      alert('El juego ya comenzÃ³');
      return;
    }
    if (!roomData.players.includes(playerName)) {
      roomData.players.push(playerName);
      storage.set(`room_${roomCode.toUpperCase()}`, JSON.stringify(roomData));
    }
    setCurrentRoom(roomCode.toUpperCase());
    setPlayers(roomData.players);
    setScreen('lobby');
  };

  const startGame = () => {
    const roomResult = storage.get(`room_${currentRoom}`);
    const roomData = JSON.parse(roomResult.value);
    roomData.started = true;
    storage.set(`room_${currentRoom}`, JSON.stringify(roomData));
    
    setGameStarted(true);
    setQuestionNumber(0);
    setAnimate(true);
    setTimeout(() => loadQuestion(0), 500);
  };

  const loadQuestion = (index) => {
    const categories = Object.keys(QUESTIONS);
    const category = categories[index % categories.length];
    const questions = QUESTIONS[category];
    const question = questions[Math.floor(Math.random() * questions.length)];
    setCurrentQuestion({ ...question, category });
    setTimeLeft(15);
    setSelectedAnswer(null);
    setShowResult(false);
    setAnimate(true);
    setTimeout(() => setAnimate(false), 500);
  };

  const handleAnswerSubmit = () => {
    if (showResult) return;
    setShowResult(true);
    
    if (selectedAnswer === currentQuestion.c) {
      const basePoints = Math.max(100, 1000 - (15 - timeLeft) * 60);
      const bonus = streakBonus * 100;
      const points = basePoints + bonus;
      setStreakBonus(streakBonus + 1);
      
      const newScore = (scores[playerName] || 0) + points;
      setScores({ ...scores, [playerName]: newScore });
    } else {
      setStreakBonus(0);
    }

    setTimeout(() => {
      const nextQ = questionNumber + 1;
      if (nextQ >= 6) {
        setGameFinished(true);
      } else {
        setQuestionNumber(nextQ);
        loadQuestion(nextQ);
      }
    }, 3000);
  };

  const resetGame = () => {
    setScreen('login');
    setPlayerName('');
    setRoomCode('');
    setCurrentRoom(null);
    setPlayers([]);
    setGameStarted(false);
    setCurrentQuestion(null);
    setSelectedAnswer(null);
    setTimeLeft(15);
    setScores({});
    setShowResult(false);
    setQuestionNumber(0);
    setGameFinished(false);
    setStreakBonus(0);
  };

  // Pantalla de Login
  if (screen === 'login') {
    return (
      <div className="screen-container gradient-purple">
        <div className="background-blobs">
          <div className="blob blob-1"></div>
          <div className="blob blob-2"></div>
          <div className="blob blob-3"></div>
        </div>
        
        <div className="card glass-card">
          <div className="text-center mb-8 bounce">
            <div className="icon-container">
              <Trophy className="icon-large text-yellow animate-pulse" />
              <Sparkles className="icon-small text-yellow spin-slow" />
            </div>
            <h1 className="title-mega gradient-text">TRIVIA ONLINE</h1>
            <p className="subtitle">Â¡Demuestra cuÃ¡nto sabes!</p>
          </div>

          <div className="form-container">
            <input
              type="text"
              placeholder="Tu nombre"
              value={playerName}
              onChange={(e) => setPlayerName(e.target.value)}
              className="input-field"
            />

            <button onClick={createRoom} className="btn btn-primary">
              <Play className="w-6 h-6" />
              CREAR SALA
            </button>

            <div className="divider">
              <span>O ÃšNETE</span>
            </div>

            <input
              type="text"
              placeholder="CÃ“DIGO DE SALA"
              value={roomCode}
              onChange={(e) => setRoomCode(e.target.value.toUpperCase())}
              className="input-field text-center"
              maxLength={6}
            />

            <button onClick={joinRoom} className="btn btn-secondary">
              <LogIn className="w-6 h-6" />
              UNIRSE
            </button>
          </div>
        </div>
      </div>
    );
  }

  // Pantalla de Lobby
  if (screen === 'lobby') {
    return (
      <div className="screen-container gradient-purple">
        <div className="background-stars">
          {[...Array(20)].map((_, i) => (
            <div key={i} className="star" style={{
              width: Math.random() * 100 + 50,
              height: Math.random() * 100 + 50,
              left: `${Math.random() * 100}%`,
              top: `${Math.random() * 100}%`,
              animationDelay: `${Math.random() * 2}s`
            }} />
          ))}
        </div>

        <div className="card glass-card">
          <div className="text-center mb-6">
            <Users className="icon-medium text-cyan mx-auto mb-3 bounce" />
            <h2 className="title-large">SALA DE ESPERA</h2>
            <div className="room-code">
              <p className="text-sm">CÃ“DIGO</p>
              <p className="code-display">{currentRoom}</p>
            </div>
          </div>

          <div className="mb-6">
            <div className="section-header">
              <Target className="w-6 h-6 text-pink" />
              <h3>JUGADORES ({players.length})</h3>
            </div>
            <div className="players-list">
              {players.map((player, i) => (
                <div key={i} className="player-card">
                  <div className="player-avatar">
                    {player.charAt(0).toUpperCase()}
                  </div>
                  <span className="player-name">{player}</span>
                  {player === players[0] && (
                    <div className="host-badge">
                      <Crown className="w-4 h-4" />
                      <span>HOST</span>
                    </div>
                  )}
                </div>
              ))}
            </div>
          </div>

          {players[0] === playerName && (
            <button onClick={startGame} className="btn btn-success mb-3">
              <Zap className="w-6 h-6" />
              Â¡COMENZAR!
            </button>
          )}

          <button onClick={resetGame} className="btn btn-outline">
            SALIR
          </button>
        </div>
      </div>
    );
  }

  // Pantalla de juego terminado
  if (gameFinished) {
    const sortedPlayers = Object.entries(scores).sort((a, b) => b[1] - a[1]);
    return (
      <div className="screen-container gradient-purple">
        <div className="confetti-container">
          {[...Array(50)].map((_, i) => (
            <div key={i} className="confetti" style={{
              left: `${Math.random() * 100}%`,
              top: `${Math.random() * 100}%`,
              animationDelay: `${Math.random() * 2}s`
            }}>
              {['ðŸ†', 'â­', 'ðŸŽ‰', 'âœ¨', 'ðŸŽŠ'][Math.floor(Math.random() * 5)]}
            </div>
          ))}
        </div>

        <div className="card glass-card">
          <div className="text-center mb-8">
            <Trophy className="icon-mega text-yellow mb-4 bounce" />
            <h2 className="title-mega gradient-text">Â¡FINAL!</h2>
            <p className="subtitle">Resultados</p>
          </div>

          <div className="results-list">
            {sortedPlayers.map(([player, score], i) => (
              <div key={player} className={`result-card rank-${i + 1}`}>
                <div className="rank-number">{i + 1}</div>
                <div className="result-info">
                  <p className="result-name">{player}</p>
                  {i === 0 && <p className="champion-badge">ðŸ‘‘ CAMPEÃ“N</p>}
                </div>
                <div className="result-score">
                  <p className="score-value">{score}</p>
                  <p className="score-label">puntos</p>
                </div>
              </div>
            ))}
          </div>

          <button onClick={resetGame} className="btn btn-primary">
            JUGAR DE NUEVO
          </button>
        </div>
      </div>
    );
  }

  // Pantalla de pregunta
  if (gameStarted && currentQuestion) {
    const categoryData = CATEGORIES[currentQuestion.category];
    const progress = ((15 - timeLeft) / 15) * 100;
    
    return (
      <div className={`screen-container gradient-${currentQuestion.category}`}>
        <div className="category-icons">
          <div className="category-icon left">{categoryData.icon}</div>
          <div className="category-icon right">{categoryData.emoji}</div>
        </div>

        <div className={`card glass-card question-card ${animate ? 'card-animate' : ''}`}>
          <div className="question-header">
            <div className="category-badge">
              <div className="category-icon-box">{categoryData.icon}</div>
              <div>
                <p className="category-label">CATEGORÃA</p>
                <p className="category-name">{categoryData.name}</p>
              </div>
            </div>
            
            <div className="timer-section">
              {streakBonus > 0 && (
                <div className="streak-badge">ðŸ”¥ x{streakBonus}</div>
              )}
              <div className="timer-box">
                <div className="timer-display">
                  <Clock className="w-5 h-5" />
                  <span>{timeLeft}s</span>
                </div>
                <div className="progress-bar">
                  <div className="progress-fill" style={{ width: `${100 - progress}%` }}></div>
                </div>
              </div>
            </div>
          </div>

          <div className="progress-dots">
            {[...Array(6)].map((_, i) => (
              <div key={i} className={`dot ${i < questionNumber ? 'done' : i === questionNumber ? 'active' : ''}`}></div>
            ))}
          </div>
          <p className="question-counter">Pregunta {questionNumber + 1} de 6</p>

          <div className="question-box">
            <h3 className="question-text">{currentQuestion.q}</h3>
          </div>

          <div className="answers-grid">
            {currentQuestion.a.map((answer, i) => (
              <button
                key={i}
                onClick={() => !showResult && setSelectedAnswer(i)}
                disabled={showResult}
                className={`answer-btn ${
                  showResult
                    ? i === currentQuestion.c
                      ? 'correct'
                      : selectedAnswer === i
                      ? 'incorrect'
                      : 'disabled'
                    : selectedAnswer === i
                    ? 'selected'
                    : ''
                }`}
              >
                {answer}
              </button>
            ))}
          </div>

          {selectedAnswer !== null && !showResult && (
            <button onClick={handleAnswerSubmit} className="btn btn-success">
              Confirmar Respuesta
            </button>
          )}

          <div className="score-display">
            <p className="score-label">Tu puntuaciÃ³n</p>
            <p className="score-value">{scores[playerName] || 0}</p>
          </div>
        </div>
      </div>
    );
  }

  return null;
}

export default App;
