import React, { useState, useEffect } from 'react';
import { Play, UserPlus, Trash2, Send, ArrowRight, ZoomIn, ZoomOut, Maximize2, Trophy, Star, Zap, Heart } from 'lucide-react';

const COLORS = [
  { name: 'Rojo', value: '#E74C3C', light: '#EC7063', dark: '#C0392B', gradient: 'linear-gradient(135deg, #E74C3C, #C0392B)' },
  { name: 'Naranja', value: '#E67E22', light: '#F39C12', dark: '#D35400', gradient: 'linear-gradient(135deg, #E67E22, #D35400)' },
  { name: 'Amarillo', value: '#F1C40F', light: '#F4D03F', dark: '#F39C12', gradient: 'linear-gradient(135deg, #F1C40F, #F39C12)' },
  { name: 'Verde', value: '#27AE60', light: '#52BE80', dark: '#1E8449', gradient: 'linear-gradient(135deg, #27AE60, #1E8449)' },
  { name: 'Azul', value: '#3498DB', light: '#5DADE2', dark: '#2874A6', gradient: 'linear-gradient(135deg, #3498DB, #2874A6)' },
  { name: 'Morado', value: '#9B59B6', light: '#AF7AC5', dark: '#7D3C98', gradient: 'linear-gradient(135deg, #9B59B6, #7D3C98)' }
];

const ROUNDS = [
  { id: 1, name: 'Expectativas', question: 'Â¿CuÃ¡l es tu objetivo personal en este equipo?', color: '#F1C40F', icon: 'ðŸŽ¯' },
  { id: 2, name: 'ContribuciÃ³n', question: 'Â¿CÃ³mo puedes ayudar a alcanzar el objetivo?', color: '#3498DB', icon: 'ðŸ’ª' },
  { id: 3, name: 'DesafÃ­os', question: 'Â¿QuÃ© obstÃ¡culos ves en el camino?', color: '#E74C3C', icon: 'ðŸ§—' }
];

function App() {
  const [screen, setScreen] = useState('setup');
  const [playerName, setPlayerName] = useState('');
  const [selectedColor, setSelectedColor] = useState(null);
  const [players, setPlayers] = useState([]);
  const [currentRound, setCurrentRound] = useState(1);
  const [currentTurnIndex, setCurrentTurnIndex] = useState(0);
  const [playerAnswers, setPlayerAnswers] = useState({});
  const [currentAnswer, setCurrentAnswer] = useState('');
  const [showRoundResults, setShowRoundResults] = useState(false);
  const [gameFinished, setGameFinished] = useState(false);
  const [zoom, setZoom] = useState(1);
  const [boardPosition, setBoardPosition] = useState({ x: 0, y: 0 });
  const [isDragging, setIsDragging] = useState(false);
  const [dragStart, setDragStart] = useState({ x: 0, y: 0 });
  const [showCelebration, setShowCelebration] = useState(false);

  useEffect(() => {
    const style = document.createElement('style');
    style.textContent = `
      @keyframes float { 0% { transform: translateY(0); opacity: 1; } 100% { transform: translateY(-100px); opacity: 0; } }
      @keyframes pulse { 0%, 100% { transform: scale(1); } 50% { transform: scale(1.05); } }
      @keyframes bounce { 0%, 100% { transform: translateY(0); } 50% { transform: translateY(-20px); } }
      @keyframes confetti { 0% { transform: translateY(0) rotate(0deg); opacity: 1; } 100% { transform: translateY(100vh) rotate(720deg); opacity: 0; } }
      @keyframes glow { 0%, 100% { box-shadow: 0 0 20px rgba(102, 126, 234, 0.6); } 50% { box-shadow: 0 0 40px rgba(102, 126, 234, 0.9); } }
    `;
    document.head.appendChild(style);
    return () => document.head.removeChild(style);
  }, []);

  const triggerCelebration = () => {
    setShowCelebration(true);
    setTimeout(() => setShowCelebration(false), 3000);
  };

  const addPlayer = () => {
    if (playerName.trim() && selectedColor && !players.find(p => p.color.value === selectedColor.value)) {
      setPlayers([...players, { name: playerName.trim(), color: selectedColor, position: 0 }]);
      setPlayerName('');
      setSelectedColor(null);
    }
  };

  const removePlayer = (index) => {
    setPlayers(players.filter((_, i) => i !== index));
  };

  const startGame = () => {
    if (players.length < 2) {
      alert('Necesitas al menos 2 jugadores');
      return;
    }
    const initialAnswers = {};
    players.forEach(player => {
      initialAnswers[player.name] = { 1: '', 2: '', 3: '' };
    });
    setPlayerAnswers(initialAnswers);
    setScreen('game');
    triggerCelebration();
  };

  const submitAnswer = () => {
    if (!currentAnswer.trim()) return;

    const currentPlayer = players[currentTurnIndex];
    const updatedAnswers = { ...playerAnswers };
    updatedAnswers[currentPlayer.name][currentRound] = currentAnswer;
    setPlayerAnswers(updatedAnswers);

    const updatedPlayers = [...players];
    updatedPlayers[currentTurnIndex].position = currentRound;
    setPlayers(updatedPlayers);
    setCurrentAnswer('');

    if (currentTurnIndex === players.length - 1) {
      setTimeout(() => {
        setShowRoundResults(true);
        triggerCelebration();
      }, 600);
    } else {
      setCurrentTurnIndex(currentTurnIndex + 1);
    }
  };

  const nextRound = () => {
    if (currentRound === 3) {
      setGameFinished(true);
      triggerCelebration();
    } else {
      setCurrentRound(currentRound + 1);
      setCurrentTurnIndex(0);
      setShowRoundResults(false);
    }
  };

  const resetGame = () => {
    setScreen('setup');
    setPlayers([]);
    setCurrentRound(1);
    setCurrentTurnIndex(0);
    setPlayerAnswers({});
    setShowRoundResults(false);
    setGameFinished(false);
    setZoom(1);
    setBoardPosition({ x: 0, y: 0 });
  };

  const handleMouseDown = (e) => {
    setIsDragging(true);
    setDragStart({ x: e.clientX - boardPosition.x, y: e.clientY - boardPosition.y });
  };

  const handleMouseMove = (e) => {
    if (isDragging) {
      setBoardPosition({ x: e.clientX - dragStart.x, y: e.clientY - dragStart.y });
    }
  };

  const handleMouseUp = () => {
    setIsDragging(false);
  };

  const handleZoomIn = () => setZoom(Math.min(zoom + 0.2, 2.5));
  const handleZoomOut = () => setZoom(Math.max(zoom - 0.2, 0.5));
  const resetView = () => { setZoom(1); setBoardPosition({ x: 0, y: 0 }); };

  const currentPlayer = players[currentTurnIndex];
  const roundData = ROUNDS.find(r => r.id === currentRound);

  if (screen === 'setup') {
    return (
      <div style={{minHeight: '100vh', background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)', display: 'flex', alignItems: 'center', justifyContent: 'center', padding: '2rem', position: 'relative', overflow: 'hidden'}}>
        <div style={{position: 'absolute', inset: 0, opacity: 0.1}}>
          {[...Array(20)].map((_, i) => (
            <div key={i} style={{position: 'absolute', left: `${Math.random() * 100}%`, top: `${Math.random() * 100}%`, width: `${20 + Math.random() * 40}px`, height: `${20 + Math.random() * 40}px`, background: 'white', borderRadius: '50%', animation: `float ${3 + Math.random() * 3}s ease-in-out infinite`, animationDelay: `${Math.random() * 2}s`}} />
          ))}
        </div>

        <div style={{background: 'rgba(255,255,255,0.98)', borderRadius: '2rem', padding: '2.5rem', maxWidth: '700px', width: '100%', boxShadow: '0 25px 80px rgba(0,0,0,0.4)', position: 'relative', zIndex: 1}}>
          <div style={{textAlign: 'center', marginBottom: '2rem'}}>
            <div style={{fontSize: '5rem', marginBottom: '0.5rem', animation: 'bounce 2s ease-in-out infinite'}}>ðŸš´</div>
            <h1 style={{fontSize: '3.5rem', fontWeight: '900', background: 'linear-gradient(135deg, #667eea, #764ba2)', WebkitBackgroundClip: 'text', WebkitTextFillColor: 'transparent', marginBottom: '0.5rem', letterSpacing: '-2px'}}>TÃ¡ndem</h1>
            <div style={{display: 'flex', alignItems: 'center', justifyContent: 'center', gap: '0.5rem'}}>
              <Trophy size={20} color="#F1C40F" />
              <p style={{color: '#666', fontSize: '1.2rem', fontWeight: 'bold', margin: 0}}>Juego Colaborativo</p>
              <Heart size={20} color="#E74C3C" />
            </div>
          </div>

          <div style={{marginBottom: '2rem'}}>
            <input type="text" placeholder="âœï¸ Nombre del jugador" value={playerName} onChange={(e) => setPlayerName(e.target.value)} onKeyPress={(e) => e.key === 'Enter' && addPlayer()} style={{width: '100%', padding: '1.2rem', borderRadius: '1rem', border: '3px solid #ddd', fontSize: '1.1rem', marginBottom: '1rem'}} />

            <div style={{display: 'grid', gridTemplateColumns: 'repeat(3, 1fr)', gap: '0.75rem', marginBottom: '1.5rem'}}>
              {COLORS.map(color => (
                <button key={color.value} onClick={() => setSelectedColor(color)} disabled={players.find(p => p.color.value === color.value)} style={{padding: '1.2rem', borderRadius: '1rem', border: selectedColor?.value === color.value ? '4px solid #000' : '3px solid #ddd', background: players.find(p => p.color.value === color.value) ? '#e0e0e0' : color.gradient, cursor: players.find(p => p.color.value === color.value) ? 'not-allowed' : 'pointer', opacity: players.find(p => p.color.value === color.value) ? 0.4 : 1, fontWeight: '900', fontSize: '1.1rem', color: color.value === '#F1C40F' ? '#000' : '#fff', transition: 'all 0.3s'}}>
                  {color.name}
                </button>
              ))}
            </div>

            <button onClick={addPlayer} disabled={!playerName.trim() || !selectedColor} style={{width: '100%', padding: '1.5rem', borderRadius: '1rem', border: 'none', background: playerName.trim() && selectedColor ? 'linear-gradient(135deg, #667eea, #764ba2)' : '#ccc', color: 'white', fontWeight: '900', fontSize: '1.2rem', cursor: playerName.trim() && selectedColor ? 'pointer' : 'not-allowed', display: 'flex', alignItems: 'center', justifyContent: 'center', gap: '0.75rem'}}>
              <UserPlus size={24} />Â¡AGREGAR JUGADOR!
            </button>
          </div>

          {players.length > 0 && (
            <div style={{marginBottom: '2rem'}}>
              <div style={{display: 'flex', alignItems: 'center', gap: '0.5rem', marginBottom: '1rem'}}>
                <Star size={20} color="#F1C40F" />
                <h3 style={{fontWeight: '900', color: '#333', margin: 0}}>JUGADORES ({players.length})</h3>
                <Star size={20} color="#F1C40F" />
              </div>
              <div style={{display: 'flex', flexDirection: 'column', gap: '0.75rem'}}>
                {players.map((player, i) => (
                  <div key={i} style={{display: 'flex', alignItems: 'center', gap: '1rem', padding: '1.2rem', background: player.color.gradient, borderRadius: '1rem', border: '3px solid white', boxShadow: '0 4px 15px rgba(0,0,0,0.2)'}}>
                    <div style={{fontSize: '2rem'}}>ðŸš´</div>
                    <div style={{flex: 1}}>
                      <p style={{fontWeight: '900', margin: 0, fontSize: '1.2rem', color: 'white', textShadow: '2px 2px 4px rgba(0,0,0,0.3)'}}>{player.name}</p>
                      <p style={{fontSize: '0.9rem', color: 'rgba(255,255,255,0.9)', margin: 0}}>{player.color.name}</p>
                    </div>
                    <button onClick={() => removePlayer(i)} style={{background: 'rgba(255,255,255,0.3)', color: 'white', border: '2px solid white', borderRadius: '0.75rem', padding: '0.75rem', cursor: 'pointer'}}>
                      <Trash2 size={18} />
                    </button>
                  </div>
                ))}
              </div>
            </div>
          )}

          {players.length >= 2 && (
            <button onClick={startGame} style={{width: '100%', padding: '2rem', borderRadius: '1rem', border: 'none', background: 'linear-gradient(135deg, #11998e, #38ef7d)', color: 'white', fontSize: '1.5rem', fontWeight: '900', cursor: 'pointer', display: 'flex', alignItems: 'center', justifyContent: 'center', gap: '1rem', animation: 'glow 2s ease-in-out infinite'}}>
              <Play size={32} />Â¡COMENZAR TÃNDEM!<Zap size={32} />
            </button>
          )}
        </div>
      </div>
    );
  }

  if (showRoundResults) {
    return (
      <div style={{minHeight: '100vh', background: `linear-gradient(135deg, ${roundData.color}dd, ${roundData.color}55)`, display: 'flex', alignItems: 'center', justifyContent: 'center', padding: '2rem', position: 'relative', overflow: 'hidden'}}>
        {showCelebration && [...Array(50)].map((_, i) => (
          <div key={i} style={{position: 'absolute', left: `${Math.random() * 100}%`, top: '-10%', fontSize: '2rem', animation: `confetti ${2 + Math.random() * 2}s linear forwards`, animationDelay: `${Math.random() * 0.5}s`}}>
            {['ðŸŽ‰', 'ðŸŽŠ', 'â­', 'âœ¨', 'ðŸ†', 'ðŸŽ¯'][Math.floor(Math.random() * 6)]}
          </div>
        ))}

        <div style={{background: 'white', borderRadius: '2rem', padding: '2.5rem', maxWidth: '900px', width: '100%', boxShadow: '0 25px 80px rgba(0,0,0,0.4)'}}>
